import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function GameDevelopment() {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const images = [
    '../images/Archers_ascent/archer1.jpg',
    '../images/Archers_ascent/archer2.jpg',
    '/path/to/image3.jpg',
    // Add more image paths as needed
  ];

  const nextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const prevImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  return (
    <div className="project-page game-development">
      <h1>Game Development</h1>
      <p className="intro">
        Game development has been a passionate pursuit of mine, allowing me to combine 
        creativity with technical skills to create engaging interactive experiences.
      </p>

      <section className="games-section">
        <h2 className="section-title">Published Games</h2>
        
        <div className="game game-with-slideshow">
          <div className="slideshow">
            <img src={images[currentImageIndex]} alt={`Archers Ascent screenshot ${currentImageIndex + 1}`} />
            <button onClick={prevImage} className="slideshow-button prev">&lt;</button>
            <button onClick={nextImage} className="slideshow-button next">&gt;</button>
          </div>
          <div className="game-content">
            <h3 className="game-title">Archers Ascent(VR)</h3>
            <p className="game-description">
              Archer's Ascent was one of the first games I published on the meta quest store. It was a zombie survival game where you're an archer and try to see how long you can survive. The biggest takeaway from producing this game was learning how to create large scale projects with multiple components interacting with each other. In addition, it taught me how to adapt very quickly to new software styles as this was the first VR game I built.
            </p>
          </div>
        </div>

        <div className="game">
          <h3 className="game-title">Game 2 Title</h3>
          <p className="game-description">
            [Brief description of Game 2. Highlight its key features and what makes it unique.]
          </p>
        </div>

        <div className="game">
          <h3 className="game-title">Game 3 Title</h3>
          <p className="game-description">
            [Brief description of Game 3. Discuss its gameplay, target audience, or any interesting development challenges.]
          </p>
        </div>
      </section>

      <Link to="/" className="back-link">Back to Main Page</Link>
    </div>
  );
}

export default GameDevelopment;
